import styled from "styled-components";
export const Button = styled.button`
  width: 200px;
  height: 35px;
  background: white;
  color: black;
  padding-left: 5px;
  font-size: 14px;
  border: 1px solid black;
  margin-left: 10px;
  &:hover {
    background-color: black;
    color: white;
    border: none;
  }
`;
